class Queue:
    def __init__(self):
        self.front = None
        self.rear = None

    def is_empty(self):
        return self.front is None

    def is_full(self):
        return self.rear is None

    def enqueue(self, item):
        if self.is_full():
            return False

        if self.is_empty():
            self.front = self.rear = None
            return True

        self.rear = self.rear.next
        self.rear.prev = self.front
        self.front.prev = self.rear
