        item = self.front.value
        self.front = self.front.next
        self.front.prev = None
        if self.front is self.rear:
            self.rear = None
        return item

    def peek(self):
        if self.is_empty():
            return None

        return self.front.value

    def is_full_peek(self):
        return self.is_full() and self.is_empty()

    def is_empty_peek(self):
        return self.is_empty() and self.is_full()
